import React, { useEffect, useState } from 'react'
import SingleGrid from './SingleGrid'

export default function Grid({socket,user_data}) {
    const [matrix ,setMatrix] = useState([]);
    useEffect(()=>{
         socket.on('get_matrix',(mat)=>{
            console.log("got the matrix")
            setMatrix(mat);
         })
    })

  return (
    <div className="h-screen w-screen ">
                        
            {/* status section  */}
            <div className=" flex justify-center items-center flex-col gap-4 ">
            <p className="text-5xl">Welcome Username _______</p>
                <p className="text-3xl">Your X player</p>

                <p>Your Turn : _____________</p>
                <p>game Staus : _____________</p>
                {/* grid  */}
                <div className="rounded-md grid grid-cols-3 gap-3 bg-yellow-500 p-3" >
                    {/* SingleGrid  */}
                    {matrix.map((row,rowIdx) =>(
                        row.map((cell,cellIdx) =>(
                            <SingleGrid cell_value = {cell}
                            key={rowIdx+'_'+cellIdx} />
                        ))
                    ))
                        
                    }
                </div>
            
            </div>

            {/* User login status  */}
            <div className="absolute bg-white top-6 right-4"> 
                user Online status
            </div>
            
    </div>
  )
}
