import React from 'react'
import { useState } from 'react'

export default function SingleGrid({cell_value,key}) {
    const [isClicked,setClick] = useState(false)

    function handleClick(){
        if(!isClicked){
            setClick(true);
            
        }
        
    }
  return (
    <div className={` rounded-lg size-20 flex items-center justify-center ${isClicked ? 'bg-slate-300 sc' :'cursor-pointer bg-white'}`} 
    onClick={()=> handleClick()}
    key = {key}>
        <span className="text-5xl text-slate-500">{cell_value}</span> 
    </div>
  )
}
