import React from 'react'
import { useState } from 'react'

export default function SingleGrid({cell_value,key,matrix,turn,socket,room_id,rowind,colind}) {
    const [isClicked,setClick] = useState((cell_value !== ""))

    function handleClick(){
        if(!isClicked){          
          console.log("turn : ",turn)
          const newMatrix = matrix.map((row,rowInd)=>(
            row.map((cell,cellInd)=>(
              ((rowInd == rowind) && (colind == cellInd)) ? (turn): cell 
            ))
          ));
          // passing new matrix to server 
          console.log("matrix : ",newMatrix)
          socket.emit('new_matrix',newMatrix,room_id);
            
        }
        
    }
  return (
    <div className={` rounded-lg size-20 flex items-center justify-center ${isClicked ? 'bg-slate-300 sc' :'cursor-pointer bg-white'}`} 
    onClick={()=> handleClick()}
    key = {key}>
        {isClicked ? <span className="text-5xl text-slate-500">X</span> 
        :
        <span className="text-5xl text-slate-500">{cell_value}</span> }

    </div>
  )
}
