import React from 'react'
import { useState } from 'react'

export default function SingleGrid() {
    const [isClicked,setClick] = useState(false)

    function handleClick(){
        if(!isClicked){
            setClick(true);
            
        }
        
    }
  return (
    <div className={` rounded-lg size-20 flex items-center justify-center ${!isClicked && 'cursor-pointer bg-white'}`} 
    onClick={()=> handleClick()}>
        <span className="text-5xl text-slate-500">X</span>
    </div>
  )
}
