import RoomComponent from './RoomComponent'
import Grid from './Grid'
import { useEffect, useState } from 'react';
import io from 'socket.io-client'

function App() {
  const [socket,setSocket] = useState(undefined);
  const [rm_join_status,setJoinStatus] = useState({rm_status:false,data:{}});

  useEffect(()=>{
    const new_socket = io('http://localhost:3001/',{
      reconnection:false
    });
    setSocket(new_socket)
    socket.on('rm_join_cnfr',(user_data)=>{
      setJoinStatus({em_status:true,data:user_data});
    })

    // disconnecting socket 
    return () => {
      socket.disconnect();
      console.log('Socket disconnected');
    };

  },[])

  return (
        <div className=" h-screen w-screen flex bg-gradient-to-tr from-cyan-400 to-purple-600 font-mono">
          {/* <h1 className="absolute text-5xl top-0 right-0 left-0 m-auto ">Welcome to tic tac toe</h1> */}

           {/* first division  */}
          <RoomComponent />
          {/* second division grid only visible after joining room */}
          {rm_join_status ? <Grid /> :''}

        </div>
  );
}

export default App;
