import React, { useEffect, useState } from 'react'
import SingleGrid from './SingleGrid'

export default function Grid({socket,user_data,matrix,setMatrix}) {
    const [startGame,setStartGame] = useState(false);
    const [user_lis ,setUserLis] = useState([]);                  

    useEffect(()=>{
        if(!socket) return;
         socket.on('get_matrix',(mat)=>{
            console.log("got the matrix",matrix)    
            setMatrix(mat);
         })
        

        //  receving user using call back 
        socket.emit('get_user_lis',(response)=>{
            console.log('calling for data ',response);
            setUserLis(response);
        })

        // Listen for user list updates
        socket.on('update_user_lis', (updatedUserLis) => {
            console.log('updated user list', updatedUserLis);
            setUserLis(updatedUserLis);
        });

         return () => {
            socket.off('get_matrix');
          }; 
    },[socket])

  return (
    <div className="h-screen w-screen ">
                        
            {/* status section  */}
            <div className=" flex justify-center items-center flex-col gap-4 ">
            <p className="text-5xl">Welcome {user_data.data.client_user_name}</p>
                <p className="text-3xl">Your X player</p>

                <p>Your Turn : {user_data.data.client_turn}</p>
                <p>game Staus : {startGame ? 'onGoing' : 'Not Started' }</p> 
                <button className='border border-white p-2 rounded-md hover:bg-purple-500'>Star Game </button>
                {/* grid  */}
                
                
                <div className="rounded-md grid grid-cols-3 gap-3 bg-yellow-500 p-3 cursor-not-allowed"  >
                    {/* SingleGrid  */}
                    {matrix ? (
                                matrix.map((row, rowIdx) => (
                                    row.map((cell, cellIdx) => (
                                    <SingleGrid
                                        cell_value={cell}
                                        key={`${rowIdx}_${cellIdx}`}
                                        matrix={matrix}
                                        socket={socket}
                                        turn={user_data.data.client_turn}
                                        room_id={user_data.data.client_user_room_id}
                                        rowind={rowIdx}
                                        colind={cellIdx}
                                    />
                                    ))
                                ))
                                ) : (
                                <h1>Loading...</h1>
                                )}

                </div>
            
            </div>

            {/* User login status  */}
            <div className="absolute bg-white top-6 right-4"> 
                {user_lis.map(user=>(
                    
                    <p>{user}</p>
                    
                    
                ))}
            </div>
            
    </div>
  )
}
